import { Link } from 'react-router-dom'
import React from 'react'
import useFetch from 'hooks/useFetch'
import useLocalStorage from 'hooks/useLocalStorage'
import { useLocation, Navigate } from 'react-router-dom'
import { CurrentUserContext } from 'contexts/currentUser'
import ErrorMessages from './components/errorMessages'

const Authentication = () => {
  const path = useLocation().pathname
  const isLogin = path === '/login'
  const pageTitle = isLogin ? 'Sign In' : 'Sign Up'
  const descriptionLink = isLogin ? '/register' : '/login'
  const descriptionText = isLogin ? 'Need an account?' : 'Have an account?'
  const apiUrl = isLogin ? '/users/login' : '/users'

  const [email, setEmail] = React.useState('')
  const [password, setPassword] = React.useState('')
  const [username, setUsername] = React.useState('')

  const [errors, setErrors] = React.useState(null)

  const [isSuccessfullSubmit, setIsSuccessfullSubmit] = React.useState(false)
  const [{ response, isLoading, error }, doFetch] = useFetch(apiUrl)
  const [, setToken] = useLocalStorage('token')

  const [, dispatch] = React.useContext(CurrentUserContext)

  const handleSubmit = (evt) => {
    evt.preventDefault()
    const user = isLogin ? { email, password } : { email, password, username }
    doFetch({ method: 'post', data: { user } })
  }

  React.useEffect(() => {
    if (error) {
      setErrors(error)
      setPassword('')
    }

    if (!response) {
      return
    }

    setIsSuccessfullSubmit(true)
    setToken(response.user.token)
    dispatch({ type: 'SET_AUTHORIZED', payload: response.user })
  }, [response, error, setToken, dispatch])

  if (isSuccessfullSubmit) {
    return <Navigate to="/" />
  }

  return (
    <div className="auth-page">
      <div className="container page">
        <div className="row">
          <div className="col-md-6 offset-md-3 col-xs-12">
            <h1 className="text-xs-center">{pageTitle}</h1>
            <p className="text-xs-center">
              <Link to={descriptionLink}>{descriptionText}</Link>
            </p>
            <form onSubmit={handleSubmit}>
              {errors && <ErrorMessages errors={error.errors} />}
              <fieldset>
                {!isLogin && (
                  <fieldset className="form-group">
                    <input
                      type="text"
                      className="form-control form-control-lg"
                      placeholder="Username"
                      value={username}
                      onChange={(evt) => {
                        setUsername(evt.target.value)
                        setErrors(null)
                      }}
                    />
                  </fieldset>
                )}
                <fieldset className="form-group">
                  <input
                    type="email"
                    className="form-control form-control-lg"
                    placeholder="Email"
                    value={email}
                    onChange={(evt) => {
                      setEmail(evt.target.value)
                      setErrors(null)
                    }}
                  />
                </fieldset>
                <fieldset className="form-group">
                  <input
                    type="password"
                    className="form-control form-control-lg"
                    placeholder="Password"
                    value={[password]}
                    onChange={(evt) => {
                      setPassword(evt.target.value)
                      setErrors(null)
                    }}
                  />
                </fieldset>
                <button
                  className="btn btn-lg btn-primary pull-xs-right"
                  type="submit"
                  disabled={isLoading}
                >
                  {pageTitle}
                </button>
              </fieldset>
            </form>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Authentication
