import React from 'react'
import useFetch from 'hooks/useFetch'
import useLocalStorage from 'hooks/useLocalStorage'
import { CurrentUserContext } from 'contexts/currentUser'
import ErrorMessages from 'pages/authentication/components/errorMessages'
import { Navigate } from 'react-router-dom'

const Settings = () => {
  const [image, setImage] = React.useState('')
  const [name, setName] = React.useState('')
  const [bio, setBio] = React.useState('')
  const [email, setEmail] = React.useState('')
  const [password, setPassword] = React.useState('')
  const [isSuccessfullLogout, setIsSuccessfullLogout] = React.useState(false)

  const [currentUserState, dispatch] = React.useContext(CurrentUserContext)
  const apiUrl = '/user'
  const [{ response, error }, doFetch] = useFetch(apiUrl)
  const [, setToken] = useLocalStorage('token')

  const handleSubmit = (event) => {
    event.preventDefault()
    doFetch({
      mathod: 'put',
      data: {
        user: {
          ...currentUserState.currentUser,
          username: name,
          image,
          bio,
          email,
          password,
        },
      },
    })
  }

  const logout = (event) => {
    event.preventDefault()
    setToken('')
    dispatch({ type: 'LOGOUT' })
    setIsSuccessfullLogout(true)
  }

  React.useEffect(() => {
    if (!currentUserState.currentUser) {
      return
    }
    setImage(currentUserState.currentUser.image)
    setName(currentUserState.currentUser.username)
    setBio(
      currentUserState.currentUser.bio ? currentUserState.currentUser.bio : ''
    )
    setEmail(currentUserState.currentUser.email)
  }, [currentUserState.currentUser])

  React.useEffect(() => {
    if (!response) {
      return
    }

    dispatch({ type: 'SET_AUTHORIZED', payload: response.user })
  }, [response, dispatch])

  if (isSuccessfullLogout) {
    return <Navigate to="/" />
  }

  return (
    <div className="settings-page">
      <div className="container page">
        <div className="row">
          <div className="col-md-6 offset-md-3 col-xs-12">
            <h1 className="text-xs-center">Your settings</h1>
            {error && <ErrorMessages errors={error.errors} />}
            <form onSubmit={handleSubmit}>
              <fieldset>
                <fieldset className="form-group">
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    placeholder="URL of profile picture"
                    value={image}
                    onChange={(e) => setImage(e.target.value)}
                  />
                </fieldset>
                <fieldset className="form-group">
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    placeholder="Username"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                  />
                </fieldset>
                <fieldset className="form-group">
                  <textarea
                    type="text"
                    className="form-control form-control-lg"
                    placeholder="Short bio"
                    value={bio}
                    rows="8"
                    onChange={(e) => setBio(e.target.value)}
                  ></textarea>
                </fieldset>
                <fieldset className="form-group">
                  <input
                    type="text"
                    className="form-control form-control-lg"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </fieldset>
                <fieldset className="form-group">
                  <input
                    type="password"
                    className="form-control form-control-lg"
                    placeholder="New password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </fieldset>
                <button
                  type="submit"
                  className="btn btn-lg btn-primary pull-xs-right"
                >
                  Update settings
                </button>
              </fieldset>
            </form>
            <hr />
            <button className="btn btn-outline-danger" onClick={logout}>
              Or click here to logout.
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}

export default Settings
